import React, { useState, useEffect } from 'react';
import Nav from './Nav';
import axios from 'axios'; // Import axios for making API requests

const HomePage = () => {
  const [orders, setOrders] = useState([]); // State variable to store orders data
  const [loading, setLoading] = useState(true); // State variable to track loading state
  const [error, setError] = useState(null); // State variable to store error message

  useEffect(() => {
    const fetchOrders = async () => {
      try {
        const response = await axios.get('/get-order/123'); // Replace '123' with the actual UID
        setOrders(response.data.data); // Update orders state with fetched data
        setLoading(false); // Set loading state to false after successful data fetch
      } catch (err) {
        console.error('Error fetching orders:', err.message);
        setError('Error fetching orders. Please try again later.'); // Set error state if request fails
        setLoading(false); // Set loading state to false if request fails
      }
    };

    fetchOrders(); // Call the fetchOrders function when the component mounts
  }, []); // Empty dependency array ensures useEffect runs only once after component mounts

  return (
    <div>
      <Nav />
      <h1>Home Page</h1>
      {loading ? (
        <p>Loading...</p>
      ) : error ? (
        <p>{error}</p>
      ) : (
        <ul>
          {orders.map((order) => (
            <li key={order.orderid}>{/* Render order details here */}</li>
          ))}
        </ul>
      )}
    </div>
  );
};

export default HomePage;
