import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import './Form.css';

const AddOrder = () => {
  const [type , setType] = useState('');
  const [quantity,setQuantity ] = useState('');
  const [total_price,setTotal_Price ] = useState('');
  const navigate = useNavigate();

  const handleSubmit = () => {

  }

  return (
    <div className="login-container">
      <div className="login-content">
      <h2 style={{ marginBottom: '20px', fontSize: '24px', color: '#333',textAlign: "center" }}>Login</h2>        
      <form className="login-form">
          <div className="form-group"> 
          <select
        value={type}
        onChange={(e) => {className="form-control"
          setType(e.target.value);
          if (e.target.value === "Biryani") {
            setQuantity('200Rs'); 
          } else if (e.target.value === "Panner Pulao") {
            setQuantity(''); 
          } else if (e.target.value === "Pizza") {
            setQuantity(300); 
          } else {
            setQuantity(''); 
          }
        }}
        >
          <option value="">Select Type</option>
          <option value="Biryani">Biryani</option>
          <option value="Panner Pulao">Panner Pulao</option>
          <option value="Pizza">Pizza</option>
          </select>
           </div>
        <div className="form-group">
            <input
              type="text"
              readOnly
              value={quantity}
              onChange={(e) => setQuantity(e.target.value)}
              className="form-control"
              placeholder="Price"
            />
          </div>
          <div className="form-group">
          <input
            type="number"
            value={quantity}
            onChange={(e) => setQuantity(e.target.value)}
            className="form-control"
            placeholder="Quantity"
            min="1"
            max="10"
          />
        </div>

          <div className="form-group">
            <input
              type="quantity"
              value={quantity}
              onChange={(e) => setQuantity(e.target.value)}
              className="form-control"
              placeholder="Quantity"
            />
          </div>
          <button type="submit" className="btn btn-primary" onClick={handleSubmit}>Login</button>
        </form>
      </div>
    </div>
  );
};

export default AddOrder;
