import React, { useState } from 'react';
import './LoginPage.css';

const LoginPage = () => {
  const [username, setUsername] = useState('');
  const [password, setPassword] = useState('');

  const handleLogin = (e) => {
    e.preventDefault();
    // Add your login logic here
    console.log('Logging in with username:', username, 'and password:', password);
  };
  const handleSignup = () => {
    history.push('/signup'); // Navigate to '/signup' route
  };

  return (
    <div className="login-container">
      <div className="login-content">
      <h2 style={{ marginBottom: '20px', fontSize: '24px', color: '#333',textAlign: "center" }}>Login</h2>        
      <form onSubmit={handleLogin} className="login-form">
          <div className="form-group">
            <input
              type="text"
              value={username}
              onChange={(e) => setUsername(e.target.value)}
              className="form-control"
              placeholder="Username"
            />
          </div>
          <div className="form-group">
            <input
              type="password"
              value={password}
              onChange={(e) => setPassword(e.target.value)}
              className="form-control"
              placeholder="Password"
            />
          </div>
          <button type="submit" className="btn btn-primary">Login</button>
        </form>
        <div className="forgot-password">
          <span>New To site??</span>
          <button type="button" className="btn btn-secondary" onClick={handleSignup}>Register</button>
        </div>
      </div>
    </div>
  );
};

export default LoginPage;
