import Cookies from 'js-cookie';
import { jwtDecode } from 'jwt-decode';
import React, { useEffect, useState } from 'react';
import Nav from './Nav';
import './Orders.css';
import Vooshapi from './Vooshapi';

const HomePage = () => {
  const [orders, setOrders] = useState([]); 
  const [loading, setLoading] = useState(true); 

  useEffect(() => {
    const token = Cookies.get('token');
    const decodedToken = jwtDecode(token);
    const uid = decodedToken.uid;
    console.log(uid)

    const fetchOrders = async () => {
      try {
        const response = await Vooshapi.get(`/get-order/${uid}`, {
          headers: {
            Authorization: `${token}`, 
          }
        });
        setOrders(response.data.data);
        setLoading(false);
      } catch (err) {
        console.error('Error fetching orders:', err.message);
        setLoading(false);
      }
    };

    fetchOrders();
  }, []);

  console.log(orders)

  return (
<div className="container">
  <Nav />
  <h1 style={{ fontSize: '30px', textAlign: 'center' }}>Your orders</h1>
  {loading ? (
    <p>Loading...</p>
  ) : (
    orders.length === 0 ? (
      <h1 style={{ fontSize: '15px', textAlign: 'center' }}>No orders found!Please please your orders</h1>
      ) : (
      <table className="order-table">
        <thead>
          <tr>
            <th>Order ID</th>
            <th>User ID</th>
            <th>Type</th>
            <th>Total Number</th> {/* Updated column name */}
            <th>Price</th>
          </tr>
        </thead>
        <tbody>
          {orders.map(order => (
            <tr key={order.orderid}>
              <td>{order.orderid}</td>
              <td>{order.uid}</td>
              <td>{order.type}</td>
              <td>{order.total_number}</td> {/* Updated column name */}
              <td>{order.price}</td>
            </tr>
          ))}
        </tbody>
      </table>
    )
  )}
</div>

  );
};

export default HomePage;
