import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import './Form.css';

const AddOrder = () => {
  const [type , setType] = useState('');
  const [quantity,setQuantity ] = useState('');
  const [price,setPrice] = useState('');
  const [total_price,setTotal_Price ] = useState('');
  const navigate = useNavigate();

  const handleSubmit = () => {

  }

  return (
    <div className="login-container">
      <div className="login-content">
      <h2 style={{ marginBottom: '20px', fontSize: '24px', color: '#333',textAlign: "center" }}>Login</h2>        
      <form className="login-form">
          <div className="form-group">  Food 
          <select
            value={type}
            onChange={(e) => {  
              setType(e.target.value);
              if (e.target.value === "Biryani") {
                setPrice('200Rs'); 
              } else if (e.target.value === "Panner Pulao") {
                setPrice('250Rs'); 
              } else if (e.target.value === "Pizza") {
                setPrice('300Rs'); 
              } else {
                setPrice(''); 
              }
            }}
            className="form-control"
            >
          <option value="">Select Type</option>
          <option value="Biryani">Biryani</option>
          <option value="Panner Pulao">Panner Pulao</option>
          <option value="Pizza">Pizza</option>
          </select>
           </div>
        <div className="form-group">
            <input
              type="text"
              readOnly
              value={price}
              onChange={(e) => setPrice(e.target.value)}
              className="form-control"
              placeholder="Price"
            />
          </div>
          <div className="form-group">
        <input
          type="number"
          value={quantity}
          onChange={(e) => setQuantity(e.target.value)}
          className="form-control"
          placeholder="Quantity"
          />
      </div>

          <div className="form-group">
            <input
              type="quantity"
              value={quantity}
              onChange={(e) => setQuantity(e.target.value)}
              className="form-control"
              placeholder="Quantity"
            />
          </div>
          <button type="submit" className="btn btn-primary" onClick={handleSubmit}>Login</button>
        </form>
      </div>
    </div>
  );
};

export default AddOrder;
